import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Redirect,
  Route,
  Link,
  Switch,
} from "react-router-dom";
import {
  Navbar,
  Nav,
  Container,
  Offcanvas,
  NavDropdown,
  Form,
  FormControl,
  Button,
  Modal,
  Row,
  Col,
  Card,
} from "react-bootstrap";

import Home from "./Movies.jsx";
import About from "./About.jsx";
import Player from "./Player.jsx";
import Mdata from "./Mdata.jsx";
import logo from "../logo.svg";
import Login from "./Login.jsx";
import history from "./history";

function Navbars() {
  const [search, setSearch] = useState([]);
  const [lgShow, setLgShow] = useState(false);
  const [print, setPrint] = useState(false);
  // let history = useHistory();

  function getData(e) {
    console.log(e.target.value);

    let input = e.target.value;
    let len = input.length;
    console.log(len);
    if (len > 0) {
      setPrint(true);

      fetch(
        ` http://www.omdbapi.com/?s=${e.target.value}&apikey=9f5e5150`
      ).then((response) => {
        response.json().then((resp) => {
          setSearch(resp.Search);
          console.log(resp.Search);
        });
      });
    } else {
      setPrint(false);
    }
  }

  return (
    <div>
      <Router>
        <div>
          <Navbar bg="dark" variant="dark" expand={false}>
            <Container fluid>
              <Navbar.Brand className="mx-2" as={Link} to={"/"}>
                {" "}
                <img src={logo} alt="logo" width="80px" /> Revival Pictures
              </Navbar.Brand>
              <div>
                <Button
                  variant="muted me-2"
                  onClick={() => {
                    setLgShow(true);
                  }}
                >
                  <i class="fas fa-search text-white"></i>
                </Button>
                <Navbar.Toggle aria-controls="offcanvasNavbar" />
              </div>
              <Navbar.Offcanvas
                id="offcanvasNavbar"
                aria-labelledby="offcanvasNavbarLabel"
                placement="end"
              >
                <Offcanvas.Header closeButton>
                  <Offcanvas.Title id="offcanvasNavbarLabel">
                    Menu
                  </Offcanvas.Title>
                </Offcanvas.Header>
                <Offcanvas.Body>
                  <Nav className="justify-content-end flex-grow-1 pe-3">
                    <Nav.Link as={Link} to={"/Home"}>
                      Home
                    </Nav.Link>
                    <Nav.Link as={Link} to={"/about"}>
                      About
                    </Nav.Link>
                    <NavDropdown title="Dropdown" id="offcanvasNavbarDropdown">
                      <NavDropdown.Item href="#action3">
                        Action
                      </NavDropdown.Item>
                      <NavDropdown.Item href="#action4">
                        Another action
                      </NavDropdown.Item>
                      <NavDropdown.Divider />
                      <NavDropdown.Item href="#action5">
                        Something else here
                      </NavDropdown.Item>
                    </NavDropdown>
                  </Nav>
                </Offcanvas.Body>
              </Navbar.Offcanvas>
            </Container>
          </Navbar>
        </div>

        <div>
          <Switch>
            {/* <Route path="/"><First/> </Route> */}
            <Route exact path="/" component={() => <Redirect to="/Home" />} />
            <Route path="/login">
              <Login />{" "}
            </Route>
            <Route path="/Home">
              <Home />{" "}
            </Route>
            <Route path="/about">
              <About />
            </Route>
            <Route path="/contact">
              <Player />
            </Route>
            <Route path="/mdata">
              <Mdata />
            </Route>
          </Switch>
        </div>
      </Router>

      <Modal
        size="lg"
        show={lgShow}
        onHide={() => setLgShow(false)}
        aria-labelledby="example-modal-sizes-title-lg"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-modal-sizes-title-lg">
            <h3>Search Movies/series</h3>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form className="">
            <FormControl
              type="search"
              placeholder="Search"
              className="me-2 w-100"
              aria-label="Search"
              onChange={getData}
            />
          </Form>
          

          {print ? (
            <Row className="mt-2 justify-content-between mx-4">
              {search?.map((item) => (
                <Col className="p-0 ">
                  <Card
                    className=" text-white mb-3 zoom1"
                    style={{ width: "12rem" }}
                    onClick={() =>
                      history.push({ pathname: "/mdata", search: item.imdbID })
                    }
                  >
                    <Card.Img src={item.Poster} alt="Card image" />

                    <div className="row justify-content-center info  mx-3">
                      <i
                        class="far fa-play-circle"
                        style={{ fontSize: "2rem" }}
                      ></i>
                      <p className="m-0">{item.Title}</p>
                      <small style={{ fontSize: "0.6rem" }}>
                        {item.Year} . {item.Type} . {item.imdbID}
                      </small>
                    </div>
                  </Card>
                </Col>
              ))}
            </Row>
          ) : null}
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default Navbars;
